    vec4 texColor = texture2D(baseColorMap, FSInput_texcoord);
    texColor.rgb = SRGBToLinear(texColor.rgb);
    color *= texColor;
  #endif
  #if USE_ALPHA_TEST
    if (color.ALPHA_TEST_CHANNEL < colorScaleAndCutoff.w) discard;
  #endif
  return color;
}
